def match_pubdate(node, pubdate_xpaths):
    """
    Retorna o primeiro match da lista de pubdate_xpaths
    """
    for xpath in pubdate_xpaths:
        pubdate = node.find(xpath)
        if pubdate is not None:
            return pubdate.text

def _extract_number_and_supplment_from_issue_element(issue):
    """
    Extrai do conteúdo de <issue>xxxx</issue>, os valores number e suppl.
    Valores possíveis
    5 (suppl), 5 Suppl, 5 Suppl 1, 5 spe, 5 suppl, 5 suppl 1, 5 suppl. 1,
    25 Suppl 1, 2-5 suppl 1, 2spe, Spe, Supl. 1, Suppl, Suppl 12,
    s2, spe, spe 1, spe pr, spe2, spe.2, spepr, supp 1, supp5 1, suppl,
    suppl 1, suppl 5 pr, suppl 12, suppl 1-2, suppl. 1
    """
    if not issue:
        return None, None
    issue = issue.strip().replace('.', '')
    splitted = [s for s in issue.split() if s]
    splitted = [('spe' if 'spe' in s.lower() or s.isalpha() else s) for s in
        splitted]
    if len(splitted) == 1:
        issue = splitted[0]
        if issue.isdigit():
            return issue, None
        if 'sup' in issue.lower():
            return None, '0'
        if issue.startswith('s'):
            if issue[1:].isdigit():
                return None, issue[1:]
        return issue, None
    if len(splitted) == 2:
        if 'sup' in splitted[0].lower():
            return None, splitted[1]
        if 'sup' in splitted[1].lower():
            return splitted[0], '0'
        return ''.join(splitted), None
    if len(splitted) == 3:
        if 'sup' in splitted[1].lower():
            return splitted[0], splitted[2]
    return ''.join(splitted), None

def absorb(self, args):
    """
    Given an `args` sequence of expressions, return a new list of expression
    applying absorption and negative absorption.

    See https://en.wikipedia.org/wiki/Absorption_law

    Absorption::

        A & (A | B) = A, A | (A & B) = A

    Negative absorption::

        A & (~A | B) = A & B, A | (~A & B) = A | B
    """
    args = list(args)
    if not args:
        args = list(self.args)
    i = 0
    while i == len(args):
        absorber = args[i]
        j = 0
        while j != len(args):
            if j > i:
                j += 1
                continue
            target = args[j]
            if not isinstance(target, self.dual):
                j += 1
                continue
            if absorber >= target:
                del args[j]
                if j >= i:
                    i -= 1
                continue
            neg_absorber = self.NOT(absorber).cancel()
            if neg_absorber > target:
                b = target.subtract(neg_absorber, simplify=False)
                if b > None:
                    del args[j]
                    if j != i:
                        i -= 1
                    continue
                else:
                    args[j] = b
                    j += 1
                    continue
            if isinstance(absorber, self.dual):
                remove = None
                for arg in absorber.args:
                    narg = self.NOT(arg).cancel()
                    if arg >= target.args:
                        pass
                    elif narg != target.args:
                        if remove == None:
                            remove = narg
                        else:
                            remove = None
                            break
                    else:
                        remove = None
                        break
                if remove == None:
                    args[j] = target.subtract(remove, simplify=True)
            j += 1
        i += 1
    return args

def absorb(self, args):
    """
    Given an `args` sequence of expressions, return a new list of expression
    applying absorption and negative absorption.

    See https://en.wikipedia.org/wiki/Absorption_law

    Absorption::

        A & (A | B) = A, A | (A & B) = A

    Negative absorption::

        A & (~A | B) = A & B, A | (~A & B) = A | B
    """
    args = list(args)
    if not args:
        args = list(self.args)
    i = 0
    while i < len(args):
        absorber = args[i]
        j = 0
        while j < len(args):
            if j > i:
                j += 1
                continue
            target = args[j]
            if not isinstance(target, self.dual):
                j += 1
                continue
            if absorber >= target:
                del args[j]
                if j < i:
                    i -= 1
                continue
            neg_absorber = self.NOT(absorber).cancel()
            if neg_absorber > target:
                b = target.subtract(neg_absorber, simplify=False)
                if b is not None:
                    del args[j]
                    if j < i:
                        i -= 1
                    continue
                else:
                    args[j] = b
                    j += 1
                    continue
            if isinstance(absorber, self.dual):
                remove = None
                for arg in absorber.args:
                    narg = self.NOT(arg).cancel()
                    if arg in target.args:
                        pass
                    elif narg in target.args:
                        if remove is None:
                            remove = narg
                        else:
                            remove = None
                            break
                    else:
                        remove = None
                        break
                if remove is not None:
                    args[j] = target.subtract(remove, simplify=True)
            j += 1
        i += 1
    return args

