import os

def get_versions():
    """Get version information or return default if unable to do so."""
    try:
        # Get configuration settings
        cfg = get_config()
        verbose = cfg.get('verbose', False)

        # Determine root directory of current file
        here = os.path.realpath(__file__)
        root = os.path.dirname(here)

        pieces = None

        # Method 1: Get version from VCS (Git)
        try:
            pieces = git_pieces_from_vcs(cfg['tag_prefix'], root, verbose)
        except NotThisMethod:
            if verbose:
                print("Skipping VCS method (NotThisMethod)")

        # Method 2: Get version from keywords in file
        if pieces is None:
            try:
                versionfile_path = os.path.join(root, cfg['versionfile_source'])
                keywords = get_keywords(versionfile_path)
                pieces = git_versions_from_keywords(keywords, cfg['tag_prefix'], verbose)
            except NotThisMethod:
                if verbose:
                    print("Skipping keywords method (NotThisMethod)")

        # Method 3: Get version from parent directory
        if pieces is None:
            try:
                parentdir = os.path.dirname(root)
                pieces = versions_from_parentdir(cfg['parentdir_prefix'], parentdir, verbose)
            except NotThisMethod:
                if verbose:
                    print("Skipping parentdir method (NotThisMethod)")

        if pieces is not None:
            # Render version string and return consistent dictionary
            version_str = render(pieces, cfg['style'])
            return {
                "version": version_str,
                "full-revisionid": pieces.get("long"),
                "dirty": pieces.get("dirty"),
                "error": None
            }
        else:
            # Fallback to default version information
            return {
                "version": "0+unknown",
                "full-revisionid": None,
                "dirty": None,
                "error": "unable to compute version"
            }

    except Exception as e:
        # Handle any unexpected errors gracefully
        return {
            "version": "0+unknown",
            "full-revisionid": None,
            "dirty": None,
            "error": f"unexpected error: {str(e)}"
        }

def render(pieces, style):
    """
    Render the given version pieces into the requested style.
    """
    # Normalize the style by replacing hyphens with underscores
    normalized_style = style.replace('-', '_')

    # Map normalized style names to their rendering functions
    if normalized_style == 'pep440':
        return render_pep440(pieces)
    elif normalized_style == 'pep440_pre':
        return render_pep440_pre(pieces)
    elif normalized_style == 'pep440_post_branch':
        return render_pep440_post_branch(pieces)
    elif normalized_style == 'pep440_branch':
        return render_pep440_branch(pieces)
    elif normalized_style == 'git_describe':
        return render_git_describe(pieces)
    elif normalized_style == 'git_describe_long':
        return render_git_describe_long(pieces)
    elif normalized_style == 'pep440_post':
        return render_pep440_post(pieces)
    elif normalized_style == 'pep440_old':
        return render_pep440_old(pieces)
    else:
        raise ValueError("Unknown style: %s" % style)

