def register_vcs_handler(vcs, method):
    """
    Create decorator to mark a method as the handler of a object
    """

    def decorator(func):
        def wrapper(*args, **kwargs):
            return func(*args, **kwargs)

        setattr(vcs, method, wrapper)
        return wrapper

    return decorator

import functools

def register_vcs_handler(vcs, method):
    """
    Create decorator to mark a method as the handler of a VCS.
    """

    def decorator(func):
        @functools.wraps(func)
        def wrapper(*args, **kwargs):
            return func(*args, **kwargs)

        setattr(vcs, method, wrapper)
        return wrapper

    return decorator

def register_vcs_handler(vcs, method):
    """
    Create decorator to mark a method as the handler of a VCS.
    """

    def decorator(func):
        if not hasattr(register_vcs_handler, '_vcs_handlers'):
            register_vcs_handler._vcs_handlers = {}
        _vcs_handlers = register_vcs_handler._vcs_handlers
        if vcs not in _vcs_handlers:
            _vcs_handlers[vcs] = {}
        _vcs_handlers[vcs][method] = func
        return func

    return decorator

def validate_hierarchy(self, validate_objects=True, check_digests=True, show_warnings=False):
    num_objects = 0
    good_objects = 0

    # Open the storage root
    store = ocfl_opendir(self.root)
    # Get all object paths in the storage root
    paths = store.object_paths()

    for path in paths:
        num_objects += 1
        if validate_objects:
            # Create a validator for the current object path
            validator = Validator(path)
            # Validate the object, optionally checking digests
            if validator.validate(check_digests=check_digests):
                good_objects += 1
            else:
                if show_warnings:
                    print(f"Warning: Object at {path} is invalid.")

    return num_objects, good_objects

