def validate_hierarchy(self, validate_objects=True, check_digests=True, show_warnings=False):
    num_objects = 0
    good_objects = 0

    # Open and validate the storage root
    store = ocfl_opendir(self.root)
    if store is None:
        if show_warnings:
            print(f"Warning: Storage root at {self.root} is invalid or cannot be opened.")
