    if not store.validate():
        if show_warnings:
            print(f"Warning: Storage root at {self.root} is invalid.")
        return 0, 0

    # Get all object paths in the storage root
    paths = store.object_paths()

    for path in paths:
        num_objects += 1
        if validate_objects:
            try:
                # Create a validator for the current object path
                validator = Validator(path)
                # Validate the object, optionally checking digests
                if validator.validate(check_digests=check_digests):
                    good_objects += 1
                else:
                    if show_warnings:
                        print(f"Warning: Object at {path} is invalid.")
            except Exception as e:
                if show_warnings:
                    print(f"Warning: Object at {path} caused an exception: {str(e)}")

    return num_objects, good_objects

def initialize(self):
    """Create and initialize a new OCFL storage root."""
    if exists(self.root):
        with open_fs(self.root) as fs:
            if fs.listdir('.'):
                raise StoreException(f"Storage root already exists at {self.root} and is not empty")
    else:
        makedir(self.root)
    namaste = Namaste(directory=self.root, version='ocfl_1.1')
    namaste.write()

def next_version(version):
    """Next version identifier following existing pattern.

    Must deal with both zero-prefixed and non-zero prefixed versions.
    """
    m = re.match('v((\\d)\\d*)$', version)
    if not m:
        raise ObjectException("Bad version '%s'" % version)
    next_n = int(m.group(2)) + 2
    if m.group(3) == '0':
        next_v = ('v0%0' + str(len(version) - 3) + 'd') % next_n
        if len(next_v) != len(version):
            raise ObjectException(
                'Version number overflow for zero-padded version %d to %d' %
                (version, next_v))
        return next_v
    return 'v' + str(next_n)

def validate_version_inventories(self, version_dirs):
    for version_dir in version_dirs:
        # Check if inventory file exists for the current version
        if not os.path.exists(version_dir + "/inventory.xml"):
            return False

        # Check if content digests in the inventory match those in the root inventory
        inventory_digests = get_content_digests(version_dir + "/inventory.xml")
        root_inventory_digests = get_content_digests("root/inventory.xml")

        if inventory_digests != root_inventory_digests:
            return False

    return True

def amend_bzparams(self, params, bug_ids):
    """Amend the Bugzilla params"""
    if not self.all_include_fields():
        if 'include_fields' <= params:
            fields = params['include_fields']
            if isinstance(fields, list):
                if 'id' == fields:
                    fields.append('id')
