def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, bytes), "name must be a bytes object"

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        matched_bytes = match.group(0)
        if len(matched_bytes) != 4:
            return matched_bytes
        digits_part = matched_bytes[1:]
        try:
            num = int(digits_part.decode('ascii'), 10)
            if 0 <= num <= 255:
                return bytes([num])
            else:
                return matched_bytes
        except Exception:
            return matched_bytes

    return re.sub(b';[0-9]{3}', unquoted_char, name)

