import socket

def is_local(host):
    """Checks if the host is the localhost

    :param host: The hostname or ip
    :return: True if the host is the localhost
    """
    common_localhost = ['localhost', '127.0.0.1', '::1', 'localhost.localdomain']
    if host in common_localhost:
        return True

    try:
        addr_list = socket.getaddrinfo(host, None)
    except socket.gaierror:
        return False

    for addr in addr_list:
        ip = addr[4][0]
        if ip == '::1':
            return True
        if ip.startswith('127.'):
            return True

    return False

