            replaced_text += char
        elif code_block:
            replaced_text += char
        else:
            if char.isalpha() or char.isdigit() or char == '_':
                current_word += char
            else:
                if current_word:
                    tags.add(current_word)
                    replaced_text += replacer(current_word)
                    current_word = ""
                replaced_text += char

    if current_word:
        tags.add(current_word)
        replaced_text += replacer(current_word)

    return tags, replaced_text

def process_text_links(text):
    """
    Process links in text, adding some attributes and linkifying textual links.
    """
    from django.utils.html import linkify

    def add_attributes(attrs, new=False):
        if new:  # Only modify newly created links
            # Set target attribute
            attrs['target'] = '_blank'
            # Handle rel attribute: preserve existing values and add 'nofollow'
            rel = attrs.get('rel', '')
            if rel:
                if 'nofollow' not in rel.split():
                    attrs['rel'] = rel + ' nofollow'
            else:
                attrs['rel'] = 'nofollow'
        return attrs

    return linkify(text, callbacks=[add_attributes])

