import requests
import xml.etree.ElementTree as ET
from urllib.parse import quote

def retrieve_and_parse_diaspora_webfinger(handle):
    """Retrieve and parse a remote Diaspora webfinger document.

    :arg handle: Remote handle to retrieve
    :returns: dict
    """
    # Validate handle format
    if '@' not in handle:
        return {}

    # Split handle and extract domain
    _, domain = handle.split('@', 1)

    # Convert internationalized domain to ASCII
    try:
        domain_ascii = domain.encode('idna').decode('ascii')
    except UnicodeError:
        domain_ascii = domain

    # Build URL with encoded parameters
    base_url = f"https://{domain_ascii}/.well-known/webfinger"
    resource_param = quote(f"acct:{handle}")
    params = {'resource': resource_param}

    # Set proper headers for XML content negotiation
    headers = {
        'Accept': 'application/xrd+xml',
        'User-Agent': 'DiasporaWebFingerClient/1.0'
    }

    webfinger_data = {}

    try:
        # Fetch document with timeout and proper headers
        response = requests.get(
            base_url, 
            params=params, 
            headers=headers,
            timeout=10
        )
        response.raise_for_status()
    except (requests.exceptions.RequestException, requests.exceptions.Timeout):
        return webfinger_data

    # Get XML content
    xml_data = response.content

    # Parse XML with proper namespace handling
    try:
        root = ET.fromstring(xml_data)
    except ET.ParseError:
        return webfinger_data

    # Define XRD namespace
    ns = {'xrd': 'http://docs.oasis-open.org/ns/xri/xrd-1.0'}

    # Extract Alias
    alias = root.findtext('.//xrd:Alias', namespaces=ns)
    if alias:
        webfinger_data["alias"] = alias

    # Extract relevant links
    for link in root.findall('.//xrd:Link', namespaces=ns):
        rel = link.get('rel')
        href = link.get('href')
        if not rel or not href:
            continue

        if rel == "http://webfinger.net/rel/profile-page":
            webfinger_data["profile_page"] = href
        elif rel == "http://schemas.google.com/g/2010#updates-from":
            webfinger_data["updates_from"] = href

    return webfinger_data

