def _include_groups(self, parser_dict):
    """Resolves the include dict directive in the spec files."""
    if 'include' in parser_dict:
        includes = parser_dict['include']
        del parser_dict['include']

        if not isinstance(includes, (str, list)):
            raise SpecParserException("The 'include' directive must be a string or a list of strings.")

        if isinstance(includes, str):
            includes = [includes]

        base = {}
        for group_name in includes:
            if group_name not in self._groups:
                raise SpecParserException(f"Group '{group_name}' is not defined.")
            group_dict = deepcopy(self._groups[group_name])
            base.update(group_dict)

        base.update(parser_dict)
        parser_dict.clear()
        parser_dict.update(base)

