def _get_conditionally_required_args(self, command_name, options_spec, args):
    required_args = []
    # Create evaluation context with arguments first, then set protected variables
    context = {}
    context.update(args)
    context['command_name'] = command_name
    context['self'] = self
    for option_spec in options_spec:
        if 'required_when' in option_spec:
            condition = option_spec['required_when']
            if eval(condition, {}, context):
                required_args.append(option_spec['name'])
    return required_args

def validate_length_args(self, args):
    option_specs = self.get_parser_option_specs()
    errors = []
    for arg_name, value in args.items():
        if arg_name in option_specs:
            spec = option_specs[arg_name]
            if 'length' in spec:
                max_length = int(spec['length'])
                if len(value) > max_length:
                    errors.append(f"Argument '{arg_name}' exceeds maximum length of {max_length} (found {len(value)})")
    if errors:
        raise IRInvalidLengthException("; ".join(errors))

def validate_length_args(self, args):
    option_specs = self.get_parser_option_specs()
    errors = []
    for arg_name, value in args.items():
        if arg_name in option_specs:
            spec = option_specs[arg_name]
            if 'length' in spec:
                max_length = int(spec['length'])
                try:
                    value_length = len(value)
                except TypeError:
                    # Skip if we cannot get the length of the value
                    continue
                if value_length > max_length:
                    errors.append(f"Argument '{arg_name}' exceeds maximum length of {max_length} (found {value_length})")
    if errors:
        raise IRInvalidLengthException("; ".join(errors))

def validate_length_args(self, args):
    option_specs = self.get_parser_option_specs()
    errors = []
    for arg_name, value in args.items():
        if arg_name in option_specs:
            spec = option_specs[arg_name]
            if 'length' in spec:
                max_length = int(spec['length'])
                try:
                    value_length = len(value)
                except TypeError:
                    try:
                        value_length = len(str(value))
                    except Exception:
                        # Skip if we cannot get the length of the value even by converting to string
                        continue
                if value_length > max_length:
                    errors.append(f"Argument '{arg_name}' exceeds maximum length of {max_length} (found {value_length})")
    if errors:
        raise IRInvalidLengthException("; ".join(errors))

def validate_choices_args(self, args: List[str]) -> bool:
    """
    Check if value of choice arguments is one of the available choices.

    :param args: The received arguments.
    :return: True if the value of args is one of the available choices, False otherwise.
    """
    for arg in args:
        if arg not in self.choices:
            return False
    return True

def validate_min_max_args(self, args):
    """
    Check if value of the given args is between minimum and maximum values
    """
    minimum = 0
    maximum = 100

    if args < minimum or args > maximum:
        return False
    else:
        return True

def validate_min_max_args(self, args):
    """
    Check if value of arguments is between minimum and maximum values.

    :param args: The received arguments.
    """
    # Get option specifications
    option_specs = self.get_parser_option_specs()

    # Validate each argument against its min/max range
    for opt_name, value in args.items():
        if opt_name in option_specs:
            spec = option_specs[opt_name]
            min_val = spec.get('min')
            max_val = spec.get('max')

            try:
                # Check if value is below minimum
                if min_val is not None and value < min_val:
                    raise exceptions.IRInvalidMinMaxRangeException(
                        f"Value for '{opt_name}' ({value}) is below minimum ({min_val})"
                    )

                # Check if value is above maximum
                if max_val is not None and value > max_val:
                    raise exceptions.IRInvalidMinMaxRangeException(
                        f"Value for '{opt_name}' ({value}) is above maximum ({max_val})"
                    )
            except TypeError:
                # Handle type errors during comparison
                raise exceptions.IRInvalidMinMaxRangeException(
                    f"Value for '{opt_name}' has type {type(value).__name__} which is not comparable to min ({type(min_val).__name__}) or max ({type(max_val).__name__})"
                )

    # Return True if all validations pass
    return True

def get_nested_custom_and_control_args(self, args):
    control_args = {}
    nested_args = {}
    for arg in args:
        if arg.startswith("--"):
            arg = arg[2:]
            if "=" in arg:
                key, value = arg.split("=")
                control_args[key] = value
            else:
                control_args[arg] = True
        elif arg.startswith("-"):
            arg = arg[1:]
            if "=" in arg:
                key, value = arg.split("=")
                control_args[key] = value
            else:
                control_args[arg] = True
        else:
            if "=" in arg:
                key, value = arg.split("=")
                nested_args[key] = value
            else:
                nested_args[arg] = True
    return control_args, nested_args

def get_nested_custom_and_control_args(self, args):
    control_args = {}
    nested_args = {}
    for arg in args:
        if arg.startswith("--"):
            arg = arg[2:]
            if "=" in arg:
                key, value = arg.split("=", 1)
                control_args[key] = value
            else:
