import os
import yaml

def get_plugin_spec_flatten_dict(plugin_dir):
    result = {}

    for root, dirs, files in os.walk(plugin_dir):
        for file in files:
            if file.endswith(".yaml"):
                file_path = os.path.join(root, file)
                with open(file_path, "r") as f:
                    data = yaml.safe_load(f)
                    if isinstance(data, dict) and 'PLUGIN_SPEC' in data:
                        spec_data = data['PLUGIN_SPEC']
                        if isinstance(spec_data, dict):
                            # Generate unique prefix from relative file path
                            rel_path = os.path.relpath(file_path, plugin_dir)
                            base = os.path.splitext(rel_path)[0]
                            base = base.replace(os.sep, '.')
                            # Flatten with unique prefix
                            result.update(flatten_dict(spec_data, parent_key=base, sep='.'))

    return result

def flatten_dict(data, parent_key='', sep='.'):
    items = []
    for k, v in data.items():
        new_key = f"{parent_key}{sep}{k}" if parent_key else k
        if isinstance(v, dict):
            items.extend(flatten_dict(v, new_key, sep=sep).items())
        else:
            items.append((new_key, v))
    return dict(items)

