def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    it = iter(dicts)
    try:
        result = next(it).copy()
    except StopIteration:
        return {}
    for d in it:
        for k, v in d.items():
            if k in result:
                result[k] += v
            else:
                result[k] = v
    return result

