from xml.etree import ElementTree

def parse_diaspora_webfinger(document: str) -> Dict:
    # Try parsing as JSON first
    try:
        data = loads(document)
    except Exception:
        # If JSON parsing fails, try parsing as XRD using ElementTree
        try:
            # Parse the XML document
            root = ElementTree.fromstring(document)
            # Define the XRD namespace
            namespace = {'xrd': 'http://docs.oasis-open.org/ns/xri/xrd-1.0'}
            # Extract all Link elements
            links = []
            for link in root.findall('xrd:Link', namespace):
                # Collect all attributes of each Link element
                link_attrs = link.attrib
                links.append(link_attrs)
            # Construct data structure compatible with find_link
            data = {'links': links}
        except Exception as exc:
            logger.warning("Failed to parse webfinger document: %s", exc)
            return {}

    # Find the link with hcard relation
    hcard_link = find_link(data, "http://microformats.org/profile/hcard")

    if not hcard_link:
        logger.warning("No hcard link found in webfinger document")
        return {}

    return {
        'hcard_url': hcard_link.get('href')
    }

