def ansible_playbook(ir_workspace, ir_plugin, playbook_path, verbose=None, extra_vars=None, ansible_args=None):
    import subprocess
    import json
    cmd = ["ansible-playbook"]

    # Handle verbosity - only add flag if verbosity level is > 0
    if verbose and verbose > 0:
        cmd.append('-' + 'v' * min(verbose, 4))

    # Handle extra_vars - convert dict to JSON string
    if extra_vars is not None:
        extra_vars_str = json.dumps(extra_vars)
        cmd.append("--extra-vars=" + extra_vars_str)

    # Handle ansible_args - support flags, lists, and key-value pairs
    if ansible_args is not None:
        for key, value in ansible_args.items():
            if value is True:
                cmd.append("--" + key)
            elif value is False or value is None:
                continue
            elif isinstance(value, list):
                # Convert list to comma-separated string
                cmd.append("--" + key + "=" + ",".join(value))
            else:
                cmd.append("--" + key + "=" + str(value))

    cmd.append(playbook_path)

    subprocess.run(cmd, check=True)

def _convert_non_cli_args(self, parser_name, values_dict):
    """Casts arguments to correct types by modifying values_dict param.

    By default all the values are strings.

    :param parser_name: The command name, e.g. main, virsh, ospd, etc
    :param values_dict: The dict of with arguments
    """
    for opt_name, opt_value in values_dict.items():
        file_option_spec = self.spec_helper.get_option_spec(parser_name, opt_name)
        if not file_option_spec:
            continue

        option_type = file_option_spec.get('type', None)
        option_action = file_option_spec.get('action', None)

        if option_action == 'count':
            values_dict[opt_name] = int(opt_value)
        elif option_type == 'int':
            values_dict[opt_name] = int(opt_value)
        elif option_type == 'bool':
            values_dict[opt_name] = opt_value.lower() in ('true', '1', 'yes')
        elif option_type == 'list':
            values_dict[opt_name] = opt_value.split(',') if opt_value else []
    return values_dict

