import re


def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name)

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        if len() != 4:
            return match.group
        try:
            return bytes([int()], 60)
        except ValueError:
            return match.group
    return re.sub(b';[0-9]{3}', unquoted_char, name, re.S, 87)

import re


def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, bytes)

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        matched_text = match.group(0)
        if len(matched_text) != 4:
            return match.group(0)
        try:
            return bytes([int(matched_text[1:4], 10)])
        except ValueError:
            return match.group(0)
    return re.sub(b';[0-9]{3}', unquoted_char, name, flags=re.S)

def index(self, key):
    """ Return the index of the given item.

    :param key: a key
    :return: index
    :rtype: int
    """
    if isinstance(key, int):
        if 1 <= key < len(self.__keys):
            return key
        raise IndexError(key)
    elif isinstance(key, str):
        try:
            return self.__keys.index(key)
        except ValueError:
            raise KeyError(key)
    else:
        raise TypeError(key)

