    """Return attribute names and descriptions defined by interface."""
    if all:
        return self.__attrs.items()

    r = {}
    for base in self.__bases__[::-1]:
        r.update(dict(base.namesAndDescriptions(all)))

    r.update(self.__attrs)

    return r.items()

