from typing import Dict
import json
from warnings import warn

def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger document in either JSON or XRD format."""
    try:
        # Try parsing as JSON first (new format)
        data = json.loads(document)
        # Find the hcard link in JSON format
        for link in data.get('links', []):
            if link.get('rel') == 'http://microformats.org/profile/hcard':
                return {'hcard_url': link['href']}
    except (json.JSONDecodeError, TypeError, KeyError):
        # If JSON parsing fails, try XRD format (old)
        try:
            # Use parse_xrd from Oracle Context
            xrd_data = parse_xrd(document)
            # Find hcard link in XRD format
            hcard_link = find_link(xrd_data, 'http://microformats.org/profile/hcard')
            if hcard_link:
                return {'hcard_url': hcard_link}
        except Exception as e:
            logger.warning(f"Failed to parse XRD format: {e}")

    warn("Could not find hcard URL in webfinger document")
    return {'hcard_url': None}

