from typing import Dict
import json
from warnings import warn

def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger which is either in JSON format (new) or XRD (old)."""
    result = {'hcard_url': None, 'seed_url': None}

    try:
        # Try parsing as JSON first (new format)
        data = json.loads(document)
        # Find the mandatory links in JSON format
        for link in data.get('links', []):
            rel = link.get('rel')
            if rel == 'http://microformats.org/profile/hcard':
                result['hcard_url'] = link.get('href')
            elif rel == 'http://joindiaspora.com/seed_location':
                result['seed_url'] = link.get('href')
    except (json.JSONDecodeError, TypeError, KeyError):
        # If JSON parsing fails, try XRD format (old)
        try:
            # Use parse_xrd from Oracle Context
            xrd_data = parse_xrd(document)
            # Find links in XRD format
            hcard_link = find_link(xrd_data, 'http://microformats.org/profile/hcard')
            seed_link = find_link(xrd_data, 'http://joindiaspora.com/seed_location')

            if hcard_link:
                result['hcard_url'] = hcard_link
            if seed_link:
                result['seed_url'] = seed_link
        except (ValueError, KeyError, AttributeError) as e:
            warn(f"Failed to parse XRD format: {e}")

    if result['hcard_url'] is None:
        warn("Could not find hcard URL in webfinger document")
    if result['seed_url'] is None:
        warn("Could not find seed location URL in webfinger document")

