    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    meta = None
    if explicit_mc is not None:
        return explicit_mc

    # Get minimal bases to avoid redundant base classes
    bases = minimalBases(bases)

    for base in bases:
        base_meta = getattr(base, '__metaclass__', type(base))

        if meta is None:
            meta = base_meta
        elif meta is not base_meta:
            # If metaclasses differ, try to find a common metaclass
            if issubclass(meta, base_meta):
                # meta is already more specific
                pass
            elif issubclass(base_meta, meta):
                meta = base_meta
            else:
                # Incompatible metaclasses
                raise TypeError("Incompatible metaclasses found")

    if meta is None and len(bases) > 0:
        meta = type(bases[0])

    return meta if meta is not None else ClassType

