            return False

        # Validate inventory digest
        if not validate_inventory_digest(inventory_path):
            error(f"Invalid inventory digest in {version_dir}")
            return False

        # Validate as prior version (except for the first version)
        if version_dirs.index(version_dir) > 0:
            previous_version = version_dirs[version_dirs.index(version_dir) - 1]
            if not validate_as_prior_version(previous_version, version_dir):
                error(f"Invalid version sequence between {previous_version} and {version_dir}")
                return False

    if different_digests:
        warning(f"Found {len(different_digests)} different content digests: {sorted(different_digests)}")

    return True

