def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

def deep_merge_nodes(nodes):
    """
    Deep merge ruamel.yaml nodes, handling duplicate keys by merging MappingNodes
    and using the last value for other node types.
    """
    result = []
    merged_keys = {}

    # First pass: collect all keys and their values
    for node in nodes:
        for key, value in node:
            key_value = key.value

            if key_value in merged_keys:
                # If we have a MappingNode collision, prepare for deep merge
                if (isinstance(merged_keys[key_value][1], MappingNode) and 
                    isinstance(value, MappingNode)):
                    merged_keys[key_value] = (
                        key,
                        deep_merge_nodes([merged_keys[key_value][1], value])
                    )
                else:
                    # For non-MappingNode collisions, last value wins
                    merged_keys[key_value] = (key, value)
            else:
                merged_keys[key_value] = (key, value)

    # Convert merged dictionary back to list of tuples
    for key_value, (key_node, value_node) in merged_keys.items():
        result.append((key_node, value_node))

    return result

