def protocol_handlers(cls, protocol_version=None):
    if protocol_version is None:
        return None
    elif protocol_version == 1:
        return BoltProtocolHandlerV1()
    elif protocol_version == 2:
        return BoltProtocolHandlerV2()
    elif protocol_version == 3:
        return BoltProtocolHandlerV3()
    else:
        raise ValueError("Invalid protocol version")

def protocol_handlers(cls, protocol_version=None):
    if protocol_version is None:
        # Return all available protocol handlers when no version is specified
        return {
            (1, 0): BoltProtocolHandlerV1(),
            (2, 0): BoltProtocolHandlerV2(),
            (3, 0): BoltProtocolHandlerV3()
        }

    # Check that protocol_version is a tuple
    if not isinstance(protocol_version, tuple):
        raise TypeError("Protocol version must be a tuple")

    # Return specific protocol handler based on the major version number
    major_version = protocol_version[0]
    if major_version == 1:
        return {protocol_version: BoltProtocolHandlerV1()}
    elif major_version == 2:
        return {protocol_version: BoltProtocolHandlerV2()}
    elif major_version == 3:
        return {protocol_version: BoltProtocolHandlerV3()}
    else:
        # Return empty dictionary for unsupported versions
        return {}

