@staticmethod
def file_to_textbuffer(file_name, encoding):
    """
    Load a file toward a text buffer (UTF-8), using the specify encoding while reading.
    CAUTION : This will read the whole file IN MEMORY.
    :param file_name: File name.
    :type file_name: str
    :param encoding: Encoding to use.
    :type encoding: str
    :return: A text buffer or None in case of error.
    :rtype str
    """
    if not file_name or not encoding:
        logger.warning('file_to_textbuffer: Invalid parameters - file_name=%s, encoding=%s', file_name, encoding)
        return None

    if not FileUtility.is_file_exist(file_name):
        logger.warning('file_to_textbuffer: file does not exist - file_name=%s', file_name)
        return None

    rd = None
    try:
        rd = codecs.open(file_name, 'r', encoding=encoding, errors='strict')
        return rd.read()
    except IOError as e:
        logger.warning('file_to_textbuffer: IOError - %s', str(e))
        return None
    except UnicodeDecodeError as e:
        logger.warning('file_to_textbuffer: UnicodeDecodeError - Invalid encoding: %s', str(e))
        return None
    finally:
        if rd:
            try:
                rd.close()
            except Exception as e:
                logger.warning('file_to_textbuffer: Error closing file - %s', str(e))

def parser_flags(parser):
    """
    Given an argparse.ArgumentParser instance, return its argument flags in a space-separated
    string.
    """
    return ' '.join(option for action in parser._actions for option in action.option_strings)
