                invalid_options.append({
                    'option': arg_name,
                    'value': value,
                    'reason': f'Value exceeds maximum {spec["maximum"]}'
                })
                return False

            return True

        except (ValueError, TypeError):
            invalid_options.append({
                'option': arg_name,
                'value': value,
                'reason': 'Invalid numeric value'
            })
