    """Wraps the 'ansible-playbook' CLI."""
    cmd = ["ansible-playbook"]

    # Handle verbosity level
    if verbose is not None:
        # Convert verbose to proper ansible verbosity format (-v, -vv, -vvv, etc)
        verbose_level = int(verbose)
        if verbose_level > 0:
