from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")
        number = number + (len(alphabet) ** padding)
    elif number < 0:
        raise ValueError("Number must be a non-negative integer")

    output = []
    while number:
        number, digit = divmod(number, len(alphabet))
        output.append(alphabet[digit])

    if padding is not None:
        output.extend([alphabet[0]] * (padding - len(output)))

    return ''.join(reversed(output))

from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """Convert a number to a string, using the given alphabet.

    The output has the most significant digit first.
    """
    if number < 0:
        raise ValueError("Number must be a non-negative integer")

    # Handle 0 as a special case
    if number == 0:
        result = [alphabet[0]]
    else:
        result = []
        # Convert the number to the given base
        base = len(alphabet)
        while number > 0:
            number, digit = divmod(number, base)
            result.append(alphabet[digit])
        result.reverse()  # Reverse to get most significant digit first

    # Handle padding
    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")
        # Pad with the first character in the alphabet if needed
        while len(result) < padding:
            result.insert(0, alphabet[0])

    return ''.join(result)

