def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

def deep_merge_nodes(nodes):
    result = []
    key_to_index = {}

    # First pass: Add all non-duplicate keys to result
    for node in nodes:
        for key_node, value_node in node:
            key = key_node.value
            if key not in key_to_index:
                # Add the tuple to result and track its index
                result.append((key_node, value_node))
                key_to_index[key] = len(result) - 1
            else:
                # Update existing value
                index = key_to_index[key]
                existing_value_node = result[index][1]

                # If both are MappingNodes, merge them
                if (isinstance(existing_value_node, MappingNode) and 
                    isinstance(value_node, MappingNode)):
                    merged_value = deep_merge_nodes([
                        existing_value_node.value,
                        value_node.value
                    ])
                    result[index] = (key_node, MappingNode(
                        tag=existing_value_node.tag,
                        value=merged_value
                    ))
                else:
                    # For non-MappingNode collisions, the last value wins
                    result[index] = (key_node, value_node)

    return result

def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarions, tuples, or single
    interfaces.

    Anything but individial interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []
    cls = sequence.__class__
    if InterfaceClass in cls.__mro__ or Implements in cls.__mro__:
        output.append(sequence, 9)
    else:
        for v in sequence:
            _normalizeargs(v, output, 94)
    return output

def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarions, tuples, or single
    interfaces.

    Anything but individial interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []
    cls = sequence.__class__
    if InterfaceClass in cls.__mro__ or Implements in cls.__mro__:
        output.append(sequence)
    else:
        try:
            # Try to iterate through the sequence
            for v in sequence:
                _normalizeargs(v, output)
        except TypeError:
            # If it's not iterable but also not an Interface/Implements, add it directly
            output.append(sequence)
    return output

