def _fromutc(self, dt: datetime) -> datetime:
    if dt.tzinfo is not self:
        raise ValueError("fromutc: dt.tzinfo is not self")

    dtoff = dt.utcoffset()
    dtdst = dt.dst()

    if dtoff is None or dtdst is None:
        raise ValueError("fromutc: Non-None utcoffset and dst required")

    delta = dtoff - dtdst
    if delta:
        dt += delta
        dtdst = dt.dst()
        if dtdst is None:
            raise ValueError("fromutc: dst() gave None")

    # Check if we're in a fold
    if dtdst:
        return dt + dtdst
    else:
        # If dtdst is zero, we might be in a fold
        fold = not self.is_dst(dt)
        return self.enfold(dt, fold=fold)

