from sys import version_info

PY2 = version_info[0] == 2

def tzname_in_python2(namefunc):
    @wraps(namefunc)
    def wrapper(*args, **kwargs):
        result = namefunc(*args, **kwargs)
        if PY2 and isinstance(result, unicode):
            return result.encode('utf-8')
        return result
    return wrapper

