def get_nested_custom_and_control_args(self, args):
    control_args = {}
    nested_args = {}
    custom_args = {}

    for arg in self._iterate_received_arguments(args):
        if isinstance(arg, str):
            if arg.startswith(("--", "-")):
                key, value = self._parse_argument(arg.lstrip("-"))
                control_args[key] = value
            else:
                key, value = self._parse_argument(arg)
                if key in self.arg_spec:
                    nested_args[key] = value
                else:
                    custom_args[key] = value
        elif isinstance(arg, dict):
            for key, value in arg.items():
                if key in self.arg_spec:
                    nested_args[key] = value
                else:
                    custom_args[key] = value
        else:
            LOG.warning("Unexpected argument type: {}".format(type(arg)))

    return control_args, nested_args, custom_args

def _iterate_received_arguments(self, args):
    if isinstance(args, dict):
        return args.items()
    return args

def _parse_argument(self, arg):
    if "=" in arg:
        key, value = arg.split("=", 1)
        return key, value
    else:
        return arg, True

