from typing import Dict
import json

def parse_diaspora_webfinger(document: str) -> Dict:
    """
    Parse Diaspora webfinger which is either in JSON format (new) or XRD (old).

    Args:
        document: String containing the webfinger document

    Returns:
        Dict containing extracted information including hcard_url and seed_url
    """
    result = {}

    try:
        # Try to parse as JSON (new format)
        data = json.loads(document)

        # Check if the document follows the JRD format
        if not isinstance(data, dict):
            logger.warning("Invalid WebFinger document format: not a JSON object")
            return {}

        # Find the required links in the JSON
        for link in data.get('links', []):
            if not isinstance(link, dict):
                continue

            rel = link.get('rel', '')
            href = link.get('href')

            if not href:
                continue

            if rel == 'http://microformats.org/profile/hcard':
                result['hcard_url'] = href
            elif rel == 'http://joindiaspora.com/seed_location':
                result['seed_url'] = href

    except json.JSONDecodeError:
        # If JSON parsing fails, try XRD format (old)
        try:
            # Use parse_xrd from the Oracle Context
            xrd_data = parse_xrd(document)

            # Find links using find_link from the Oracle Context
