def is_local(host):
    """
    Checks if the host is the localhost

    :param host: The hostname or ip
    :return: True if the host is the localhost
    """
    import socket

    # Common localhost representations
    localhost_identifiers = ['localhost', 'localhost.localdomain', '127.0.0.1', '::1']

    # Check if host directly matches known localhost identifiers
    if host in localhost_identifiers:
        return True

    # Check if the host resolves to a local address
    try:
        # Get the hostname of the current machine
        local_hostname = socket.gethostname()

        # Check if the host matches the local hostname
        if host == local_hostname:
            return True

        # Try to resolve the given host to an IP address
        if host not in localhost_identifiers:
            try:
                host_ip = socket.gethostbyname(host)
                if host_ip in ['127.0.0.1', '::1']:
                    return True
            except socket.error:
                pass

        # Try to get the local IP addresses
        try:
            local_ips = socket.gethostbyname_ex(local_hostname)[2]
            # Check if host is in local IPs or resolves to a local IP
            if host in local_ips:
                return True
            try:
                host_ip = socket.gethostbyname(host)
                if host_ip in local_ips:
                    return True
            except socket.error:
                pass
        except socket.error:
            pass

    except socket.error:
        pass

    return False

