    """
    Create decorator to mark a method as the handler of a VCS.
    """
    def decorator(func):
        # Check if vcs is a valid object that can have attributes set
        if hasattr(vcs, '__setattr__'):
            setattr(vcs, method, func)
        else:
            # If vcs is not an object with __setattr__, store in a registry
            if not hasattr(register_vcs_handler, 'registry'):
                register_vcs_handler.registry = {}
            if vcs not in register_vcs_handler.registry:
                register_vcs_handler.registry[vcs] = {}
            register_vcs_handler.registry[vcs][method] = func
        return func
    return decorator

