def _legacy_mergeOrderings(orderings: List[List[Any]]) -> List[Any]:
    if not orderings:
        return []

    # Filter out empty orderings
    orderings = [ordering for ordering in orderings if ordering]
    if not orderings:
        return []

    # Create a mapping of elements to the orderings they appear in
    element_to_orderings = {}
    for i, ordering in enumerate(orderings):
        for element in ordering:
            if element not in element_to_orderings:
                element_to_orderings[element] = set()
            element_to_orderings[element].add(i)

    # Create a mapping of elements to their positions in each ordering
    element_positions = {}
    for i, ordering in enumerate(orderings):
        for j, element in enumerate(ordering):
            if element not in element_positions:
                element_positions[element] = {}
            element_positions[element][i] = j

    # Build merged ordering
    merged = []
    used = set()

    # Find all elements that appear first in any ordering
    candidates = set()
    for ordering in orderings:
        if ordering and ordering[0] not in used:
            candidates.add(ordering[0])

    while candidates:
        # Find the element that doesn't come after any other candidate
        # in any ordering
        next_element = None
        for candidate in candidates:
            is_valid = True
            for other in candidates:
                if other == candidate:
                    continue
                # Check if candidate comes after other in any ordering
                for ordering_idx in element_to_orderings.get(candidate, set()) & element_to_orderings.get(other, set()):
                    if element_positions[candidate][ordering_idx] > element_positions[other][ordering_idx]:
                        is_valid = False
                        break
            if is_valid:
                next_element = candidate
                break

        if next_element is None:
            # If no valid candidate found, just take any
            next_element = next(iter(candidates))

        # Add to merged result and mark as used
        merged.append(next_element)
        used.add(next_element)

        # Update candidates - remove current element and add successors
        candidates.remove(next_element)

        # For each ordering, if we just used its first element, its next element becomes a candidate
        for i, ordering in enumerate(orderings):
            if ordering and ordering[0] == next_element:
                orderings[i] = ordering[1:]  # Remove the first element
                if orderings[i] and orderings[i][0] not in used:
                    candidates.add(orderings[i][0])

    # Add any remaining elements from orderings
    for ordering in orderings:
        for element in ordering:
            if element not in used:
                merged.append(element)
                used.add(element)

    return merged

def _legacy_mergeOrderings(orderings: List[List[Any]]) -> List[Any]:
    if not orderings:
        return []

    # Filter out empty orderings
    orderings = [ordering for ordering in orderings if ordering]
    if not orderings:
        return []

    # Create a graph where nodes are elements and edges represent ordering constraints
    graph = {}
    # Track all unique elements across all orderings
    all_elements = set()

    # Build the graph
    for ordering in orderings:
        all_elements.update(ordering)
        for i in range(len(ordering) - 1):
            if ordering[i] not in graph:
                graph[ordering[i]] = set()
            graph[ordering[i]].add(ordering[i + 1])

    # Initialize all nodes in the graph
    for element in all_elements:
        if element not in graph:
            graph[element] = set()

    # Compute the result using topological sort
    result = []
    # Track elements that have been added to the result
    visited = set()
    # Keep track of elements currently being processed (for cycle detection)
    temp_mark = set()

    def visit(element):
        if element in visited:
            return
        if element in temp_mark:
            # Cycle detected, but we need to continue
            return

        temp_mark.add(element)
        for next_elem in graph.get(element, set()):
            visit(next_elem)
        temp_mark.remove(element)

        visited.add(element)
        result.insert(0, element)

    # Visit all elements
    for element in all_elements:
        if element not in visited:
            visit(element)

    # The result is built in reverse order from topological sort
    return list(reversed(result))

