    if mode is None:
        mode = "WRITE"
    elif mode != "READ" and mode != "WRITE":
        raise ValueError(f"Invalid access mode: {mode}")

    message = {"action": "begin", "mode": mode}

    if bookmarks:
        message["bookmarks"] = list(bookmarks)

    if metadata:
        if not isinstance(metadata, dict):
            raise TypeError("Metadata must be a dictionary")
        message["metadata"] = metadata

    if timeout is not None:
        if not isinstance(timeout, (int, float)) or timeout < 0:
            raise ValueError("Timeout must be a non-negative number")
        message["timeout"] = timeout

    if db is not None:
        message["db"] = db

    if imp_user is not None:
        message["imp_user"] = imp_user

    if dehydration_hooks is not None:
        message["dehydration_hooks"] = dehydration_hooks

    if hydration_hooks is not None:
        message["hydration_hooks"] = hydration_hooks

    self._append(message)

    log.debug("C: %s", message)

    return Response(**handlers)

