import argparse

def parse_subparser_arguments(unparsed_arguments, subparsers):
    parser = argparse.ArgumentParser()
    subparser_dict = {}
    for subparser_name, subparser in subparsers.items():
        subparser_dict[subparser_name] = parser.add_subparsers(dest=subparser_name)
        subparser.add_parser_arguments(subparser_dict[subparser_name])

    parsed_args = {}
    remaining_args = []
    for arg in unparsed_arguments:
        found = False
        for subparser_name, subparser in subparsers.items():
            if subparser_name in parsed_args:
                continue
            try:
                parsed_args[subparser_name] = subparser_dict[subparser_name].parse_args([arg])
                found = True
                break
            except argparse.ArgumentError:
                continue
        if not found:
            remaining_args.append(arg)

    return parsed_args, remaining_args

import argparse
from collections import OrderedDict

def parse_subparser_arguments(unparsed_arguments, subparsers):
    # Create a combined parser
    parser = argparse.ArgumentParser(add_help=False)

    # Create subparsers dictionary
    subparser_dict = {}
    for subparser_name, subparser in subparsers.items():
        subparser_dict[subparser_name] = parser.add_subparsers(dest=subparser_name)
        subparser.add_parser_arguments(subparser_dict[subparser_name])

    # Initialize results
    parsed_args = {}
    remaining_args = list(unparsed_arguments)

    # Try each subparser
    for subparser_name, subparser in subparsers.items():
        # Use parse_known_args to separate matching args from remaining args
        namespace, remaining = subparser_dict[subparser_name].parse_known_args(remaining_args)

        # Store the parsed arguments if any were found
        if vars(namespace).get(subparser_name) is not None or any(vars(namespace).values()):
            parsed_args[subparser_name] = namespace

        # Update remaining arguments
        remaining_args = remaining

    return parsed_args, remaining_args

