def namesAndDescriptions(self, all=True): # pylint:disable=redefined-builtin
    """Return attribute names and descriptions defined by interface."""
    if all:
        return self.__attrs.items()

    r = {}
    for base in self.__bases__[::-1]:
        r.update(dict(base.namesAndDescriptions(all)))

    r.update(self.__attrs)

    return r.items()

def namesAndDescriptions(self, all=True): # pylint:disable=redefined-builtin
    """Return attribute names and descriptions defined by interface."""
    if all:
        result = {}
        # Process bases in reverse order (from most specific to most general)
        for base in self.__bases__[::-1]:
            result.update(dict(base.namesAndDescriptions(all)))
        # Add own attributes, potentially overriding base attributes
        result.update(self.__attrs)
        return result.items()
    else:
        # If not all, return just this interface's attributes
        return self.__attrs.items()

