import json

def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger which is either in JSON format (new) or XRD (old)."""
    # First, try to parse as JSON
    try:
        data = json.loads(document)

        # Find the hcard URL and seed location in the links
        hcard_url = None
        seed_url = None

        for link in data.get('links', []):
            rel = link.get('rel')
            if rel == 'http://microformats.org/profile/hcard':
                hcard_url = link.get('href')
            elif rel == 'http://joindiaspora.com/seed_location':
                seed_url = link.get('href')

        if hcard_url and seed_url:
            return {
                'hcard_url': hcard_url,
                'seed_url': seed_url
            }
        else:
            logger.warning("WebFinger JSON document missing required links")

    except (json.JSONDecodeError, KeyError):
        # Try to parse as XRD (old format)
        try:
            # Using the parse_xrd function from the context
            data = parse_xrd(document)

            # Find the relevant links using the find_link function from context
            hcard_url = find_link(data, 'http://microformats.org/profile/hcard')
            seed_url = find_link(data, 'http://joindiaspora.com/seed_location')

            if hcard_url and seed_url:
                return {
                    'hcard_url': hcard_url,
                    'seed_url': seed_url
                }
            else:
                logger.warning("WebFinger XRD document missing required links")

        except Exception as ex:
            logger.warning(f"Failed to parse WebFinger document: {ex}")

    # Return empty dict if parsing fails
    return {}

