from typing import Dict
import json
import logging
import re
from xml.etree import ElementTree

# Get logger reference
logger = logging.getLogger(__name__)

def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger which is either in JSON format (new) or XRD (old)."""
    # First, try to parse as JSON
    if document.strip().startswith('{'):
        try:
            data = json.loads(document)

            # Find the hcard URL and seed location in the links
            hcard_url = None
            seed_url = None

            for link in data.get('links', []):
                rel = link.get('rel')
                if rel == 'http://microformats.org/profile/hcard':
                    hcard_url = link.get('href')
                elif rel == 'http://joindiaspora.com/seed_location':
                    seed_url = link.get('href')

            if hcard_url and seed_url:
                return {
                    'hcard_url': hcard_url,
                    'seed_url': seed_url
                }
            else:
                logger.warning("WebFinger JSON document missing required links")

        except (json.JSONDecodeError, KeyError) as ex:
            logger.warning(f"Failed to parse WebFinger JSON document: {ex}")

    # If it's not JSON or JSON parsing failed, try to parse as XRD (old format)
    try:
        # Implement our own simple XRD parser since federation.utils.xrd is not available
        def parse_xrd_document(xrd_content):
            # Handle HTML content that might contain the XRD data
            if "<html" in xrd_content:
                # Try to extract the XRD part if embedded in HTML
                match = re.search(r'<XRD[^>]*>(.*?)</XRD>', xrd_content, re.DOTALL | re.IGNORECASE)
                if match:
                    xrd_content = f'<XRD>{match.group(1)}</XRD>'
                else:
                    raise ValueError("Could not extract XRD data from HTML")

            # Parse the XRD XML
            try:
                return ElementTree.fromstring(xrd_content)
            except ElementTree.ParseError:
                raise ValueError("Invalid XRD XML format")

        def find_link_in_xrd(xrd_root, rel):
            # Find Link elements with the specified rel attribute
            for link in xrd_root.findall('.//Link'):
                if link.get('rel') == rel:
                    return link.get('href')
            return None

        # Parse the XRD document
        xrd_root = parse_xrd_document(document)

        # Find the relevant links
        hcard_url = find_link_in_xrd(xrd_root, 'http://microformats.org/profile/hcard')
        seed_url = find_link_in_xrd(xrd_root, 'http://joindiaspora.com/seed_location')

        if hcard_url and seed_url:
            return {
                'hcard_url': hcard_url,
                'seed_url': seed_url
            }
        else:
            logger.warning("WebFinger XRD document missing required links")

    except Exception as ex:
        logger.warning(f"Failed to parse WebFinger document: {ex}")

    # Return empty dict if parsing fails
    return {}

