def inject_config(self):
    """Set the environment variable for config path, if it is undefined."""
    if os.environ.get('ANSIBLE_CONFIG') is None:
        os.environ['ANSIBLE_CONFIG'] = self.ansible_config_path

        # Ensure the config file exists and has proper format
        try:
            with open(self.ansible_config_path, 'r') as config_file:
                config_content = config_file.read()

            # Initialize defaults section flag and updated content
            has_defaults_section = '[defaults]' in config_content
            has_host_key_checking = False

            # Check if host_key_checking exists in [defaults] section
            if has_defaults_section:
                lines = config_content.splitlines()
                in_defaults_section = False
                for line in lines:
                    if line.strip() == '[defaults]':
                        in_defaults_section = True
                    elif in_defaults_section and line.strip().startswith('['):
                        in_defaults_section = False  # Another section started
                    elif in_defaults_section and line.strip().startswith('host_key_checking'):
                        has_host_key_checking = True
                        break

            # Update the config file if needed
            if not has_defaults_section:
                with open(self.ansible_config_path, 'a') as config_file:
                    config_file.write('\n[defaults]\nhost_key_checking = False\n')
            elif not has_host_key_checking:
                with open(self.ansible_config_path, 'a') as config_file:
                    config_file.write('\nhost_key_checking = False\n')
        except Exception as e:
            # Handle possible file operation errors
            print(f"Warning: Could not update Ansible config: {e}")

    return os.environ['ANSIBLE_CONFIG']

