def is_local(host):
    import socket
    import platform

    # Common localhost identifiers
    localhost = ['localhost', '127.0.0.1', '::1']
    local_domain = ['localhost.localdomain']

    # Check direct string matches first
    if host in localhost or host in local_domain:
        return True

    # Check if host matches the current machine's hostname
    if host == platform.node() or host == socket.gethostname():
        return True

    # Try to get the IP address of the host and compare with local IPs
    try:
        host_ip = socket.gethostbyname(host)
        if host_ip in ['127.0.0.1', '::1']:
            return True
    except:
        pass

    # Try reverse lookup to check if hostname resolves to localhost
    try:
        host_name = socket.gethostbyaddr(host)[0]
        if host_name in localhost or host_name in local_domain or host_name == socket.gethostname():
            return True
    except:
        pass

    return False

