from collections import namedtuple

def ttl_cache(maxsize=128, ttl=600, timer=time.monotonic, typed=False):
    """
    Decorator to wrap a function with a memoizing callable that saves
    up to `maxsize` results based on a Least Recently Used (LRU)
    algorithm with a per-item time-to-live (TTL) value.
    """
    def decorator(func):
        cache = OrderedDict()
        hits = misses = 0
        CacheInfo = namedtuple("CacheInfo", ["hits", "misses", "maxsize", "currsize"])

        @wraps(func)
        def wrapper(*args, **kwargs):
            nonlocal hits, misses
            key = args if typed else tuple(args)
            if kwargs:
                key += tuple(sorted(kwargs.items()))

            if key in cache:
                if timer() - cache[key][1] < ttl:
                    hits += 1
                    return cache[key][0]
                else:
                    del cache[key]

            misses += 1
            result = func(*args, **kwargs)
            cache[key] = (result, timer())

            if len(cache) > maxsize:
                cache.popitem(last=False)

            return result

        def cache_info():
            """Return a named tuple with cache statistics."""
            return CacheInfo(hits, misses, maxsize, len(cache))

        def cache_parameters():
            """Return the current cache parameters."""
            return {"maxsize": maxsize, "ttl": ttl, "typed": typed}

        wrapper.cache_info = cache_info
        wrapper.cache_parameters = cache_parameters

        return wrapper

    return decorator

