def unit_of_work(metadata=None, timeout=None):
    def decorator(func):
        def wrapper(*args, **kwargs):
            # Validate timeout value
            if timeout is not None and timeout < 0:
                raise ValueError("Transaction timeout cannot be negative")

            # Get the transaction object from the first argument
            tx = args[0] if args else kwargs.get('tx')
            if tx is None:
                raise ValueError("Transaction object not provided")

            # Set transaction metadata if provided
            if metadata:
                if not isinstance(metadata, dict):
                    raise TypeError("Metadata must be a dictionary")
                tx.set_transaction_metadata(metadata)

            # Set transaction timeout if provided
            if timeout is not None:
                tx.set_transaction_timeout(timeout)

            # Execute the decorated function with original arguments
            return func(*args, **kwargs)

        return wrapper
    return decorator

