    """ Dynamically create a Point subclass.
    """
    def srid(self):
        try:
            return srid_map[len(self)]
        except KeyError:
            return None

    attributes = {'srid': property(srid)}

    # Process fields to create accessors
    for index, field in enumerate(fields):
        field_alias = field

        def accessor(self, i=index, f=field):
            try:
                return self[i]
            except IndexError as exc:
                raise AttributeError(f) from exc

        attributes[field_alias] = property(accessor)

    # Create the Point subclass
    cls = type(name, (Point,), attributes)

    # Update srid table with class and dimension
    with srid_table_lock:
        dim = len(fields)
        srid_table[srid] = cls, dim

    return cls

