    result = []
    for expr in args:
        # Skip if expression is not valid
        if not isinstance(expr, list):
            result.append(expr)
            continue

        # Check for regular absorption: A & (A | B) = A, A | (A & B) = A
        for other in args:
            if isinstance(other, list) and len(other) >= 2:
                # Case A & (A | B) = A
                if expr == other[0] and other[1] == '|':
                    expr = other[0]
                # Case A | (A & B) = A
                elif expr == other[0] and other[1] == '&':
                    expr = other[0]

        # Check for negative absorption: A & (~A | B) = A & B, A | (~A & B) = A | B
        for other in args:
            if isinstance(other, list) and len(other) >= 2:
                # Case A & (~A | B) = A & B
