        "%H:%M:%S",
        "%H:%M",
        "%H"
    ]

    parsed_time = None
    for fmt in time_formats:
        try:
            temp_dt = datetime.datetime.strptime(time_str, fmt)
            parsed_time = temp_dt.time()
            break
        except ValueError:
            continue

    if parsed_time is None:
        raise ValueError(f"Unable to parse time component: {time_str}")

    # Combine date and time
    result = datetime.datetime.combine(parsed_date.date(), parsed_time)

    # Handle timezone offset
