def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger which is either in JSON format (new) or XRD (old).

    Args:
        document (str): WebFinger document string in either JSON or XRD format

    Returns:
        Dict: Dictionary containing parsed WebFinger data with mandatory fields:
              - subject: WebFinger address
              - links: List of link relations containing hcard and seed_location
    """
    try:
        # First try parsing as JSON (new format)
        data = json.loads(document)

        # Validate required fields
