            logger.warning("WebFinger document is not a JSON object")
            return {}

        if "subject" not in data or "links" not in data:
            logger.warning("WebFinger document missing required fields")
            return {}

        # Find required link relations
        hcard_link = find_link(data["links"], "http://microformats.org/profile/hcard")
        seed_link = find_link(data["links"], "http://joindiaspora.com/seed_location")

        if not hcard_link or not seed_link:
            logger.warning("WebFinger document missing required link relations")
            return {}

        return data

    except JSONDecodeError:
        # If JSON parsing fails, try parsing as XRD (old format)
        try:
            return parse_xrd(document)
        except Exception as e:
            logger.warning(f"Failed to parse WebFinger document: {str(e)}")
            return {}

