import urllib.request
import urllib.error
import xml.etree.ElementTree as ET

def retrieve_and_parse_diaspora_webfinger(handle):
    """
    Retrieve and parse a remote Diaspora webfinger document.

    :arg handle: Remote handle to retrieve
    :returns: dict containing webfinger data
    """
    webfinger = {}

    try:
        url = f"https://{handle}/.well-known/webfinger"
        with urllib.request.urlopen(url) as response:
            if response.status == 200:
                xml_data = ET.fromstring(response.read().decode('utf-8'))

                for elem in xml_data.iter():
                    if elem.tag == "Link":
                        rel = elem.attrib.get("rel")
                        href = elem.attrib.get("href")
                        if rel and href:
                            webfinger[rel] = href
    except (urllib.error.URLError, ET.ParseError):
        pass

    return webfinger

